import React from 'react';
import '../static/css/navbar.css';

export default class Navbar extends React.Component {
    constructor(props) {
        super(props);

        this.state = {path: null};
    }

    componentDidMount() {
        var path = location.pathname;
        if (path[0] === '/')
            path = path.substring(1, path.length - 1).toLowerCase();

        console.log("Path:", path);

        this.setState({path: path});
    }

    render () {
        return (
            <nav className="nav">
                <ul>
                    <li>
                        <a href="/" className={(this.state === "" ? "active" : "")}>Home</a>
                    </li>
                    <li>
                        <a href="https://github.com/reecerussell">Github</a>
                    </li>
                    <li>
                        <a href="/Projects"  className={(this.state === "projects" ? "active" : "")}>Projects</a>
                    </li>
                </ul>
            </nav>
        );
    }
}
