import React from 'react';
import {Row, Col} from 'reactstrap';
import Layout from '../components/Layout';
import '../static/css/projects.css';
import fetch from 'isomorphic-unfetch';

class Project extends React.Component {

    constructor(props) {
        super(props);
    }

    render() {
        return (
            <Layout>
                <Row style={{padding:"75px 0 50px 0"}}>
                    <Col md={{offset:"3", size:"6"}}>
                        <img src={this.props.imageUrl} style={{width:"100%"}} />
                        <h1>{this.props.id}</h1>
                        <p style={{textAlign:"center"}}>
                            <a href={this.props.githubLink} class="btn-source">View Source</a>
                        </p>
                        <p>
                            {this.props.description}
                        </p>                   
                    </Col>
                </Row>
            </Layout>
        );
    }
}

Project.getInitialProps = async function({query}) {
    console.log(query);
    var data = await fetch("https://go.reecerussell.com/api/projects/" + query.id);

    var proj = await data.json();

    return proj;
}

export default Project
