import '../static/css/navbar.css';

const Navbar = (props) => {
    var path = location.href;
    if (path[0] === '/')
        path = path.substring(1, path.length - 1).toLowerCase();

    return (
        <nav className="nav">
            <ul>
                <li>
                    <a href="/" className={(path === "" ? "active" : "")}>Home</a>
                </li>
                <li>
                    <a href="https://github.com/reecerussell">Github</a>
                </li>
                <li>
                    <a href="/Projects"  className={(path === "projects" ? "active" : "")}>Projects</a>
                </li>
            </ul>
        </nav>
    );
}

export default Navbar;
