import React from 'react';
import {Row, Col} from 'reactstrap'

export default class Footer extends React.Component {
    render () {
        return (
            <footer className="container" style={{backgroundColor: "black", color: "white"}}>
                <Row>
                    <Col md="6">
                        <p style={{margin:0, padding:"5px"}}>
                            Copyright &copy; {new Date().getFullYear()}, Reece Russell
                        </p>
                    </Col>
                    <Col md="6">
                        <p style={{margin:0, padding:"5px", textAlign: "right"}}>
                            <a href="mailto:me@reece-russell.co.uk">me@reece-russell.co.uk</a>
                        </p>
                    </Col>
                </Row>
            </footer>
        )
    }
}
